		aberturaBitola: false,
		elevacaoRoda: false,
		fraturaTrilhos: false,
		objetosEstranhos: false,
		observacao: '',
		marcasCurtas: '',
		marcasLongas: '',
		marcasAbrasao: false,
		marcasQueda: false,
		semMarcas: false,
		marcasRegulares: false,
		marcasAlma: false,
		curvaOuTangente: '',
		curva: '',
		raio: '',
		tangente: '',
		rampa: '',
		tipoRampa: '',
		inspecaoCarro: dateSelect.date3,
		obsCarro: '',
		inspecaoLinha: dateSelect.date2,
		obsLinha: '',
		inspecaoCruzadaTrens: dateSelect.date4,
		juntaSolta: false,
		desnivelada: false,
		desencontrada: false,
		faltandoParafuso: false,
		madeira: false,
		concreto: false,
		plastico: false,
		aco: false,
		sequenciasDormentes: '',
		bom: false,
		ruim: false,
		laqueado: false,
		foraEixo: false,
		inexistente: false,
		domentesObs: '',
		tipoFixacao: '',
		outrosFixacao: '',
		estadoFixacao: '',
		observacaoFixacao: '',
		sinaisInclinacao: '',
		ladoExterno: false,
		ladoInterno: false,
		existePlacaApoio: '',
		brita: false,
		terra: false,
		lastroOutros: false,
		lastroNormal: false,
		fracoOmbro: false,
		fracoCentro: false,
		contaminado: false,
		lastroObs: '',
		drenagemBom: false,
		drenagemRuim: false,
		drenagemDesnecessario: false,
		drenagemPresencaLaqueado: false,
		drenagemObs: '',
