		trilhoFraturado: '',
		tipoFratura: '',
		trilhosOxidacao: '',
		trilhosDesgasteHorizontal: '',
		trilhosDesgasteVertical: '',
		estadoPod: '',
		idInvestigador1: 0,
		idInvestigador2: 0,
		custosMateriais: '0.00',
    	custosDeslocamento: '0.00',
    	custosPessoal: '0.00',
    	custosTotal: '0.00',
		limiteSegClassePOD: false
	});
	const radio_props = [
		{ label: 'Não	', value: false },
		{ label: 'Sim', value: true }
	];
	const radio_props2 = [
		{ label: '1 ', value: 1 },
		{ label: '2 ', value: 2 },
