		{ label: 'SKL ', value: 5 }
	];
	const radio_props6 = [
		{ label: 'Firme  ', value: 1 },
		{ label: 'Frouxo ', value: 2 },
		{ label: 'Faltando fixação  ', value: 3 },
		{ label: 'Movimentação (Passeio do Trilho)  ', value: 4 }
	];
	const radio_props7 = [
		{ label: 'Carbono ', value: 1 },
		{ label: 'Tratado ', value: 2 },
		{ label: 'Liga  ', value: 3 }
	];
	const radio_props8 = [
		{ label: 'TR25 ', value: 25 },
		{ label: 'TR32 ', value: 32 },
		{ label: 'TR37 ', value: 37 },
