		{ label: 'TR50 ', value: 50 },
		{ label: 'TR57 ', value: 57 },
		{ label: 'TR68 ', value: 68 },
		{ label: 'UIC60', value: 60 }
	];
	const radio_props9 = [
		{ label: 'Seco ', value: 1 },
		{ label: 'Lubrificado ', value: 2 },
		{ label: 'Excesso de Lubrificação  ', value: 3 }
	];
	const radio_props10 = [
		{ label: 'Novo ', value: 1 },
		{ label: 'Esmagado ', value: 2 },
		{ label: 'Reemprego  ', value: 3 },
		{ label: 'Patilhado  ', value: 4 },
		{ label: 'Esmerilhado  ', value: 5 },
		{ label: 'Soldado  ', value: 6 }
	];
	const radio_props11 = [
		{ label: 'Classe 1', value: true },
		{ label: 'Classe 2', value: false }
	];
