import React, { useState } from 'react';
import AppLoading from 'expo-app-loading';
import { Text, Button, Alert, View, TextInput, KeyboardAvoidingView, TouchableOpacity, ActivityIndicator, StyleSheet, Image } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { createAppContainer } from 'react-navigation';
import { createStackNavigator, HeaderBackButton } from 'react-navigation-stack';
import { startApp, getAssetId, setAssetId, getAlbumId, setAlbumId } from './db/sqliteConnection';
import { logIn, checkLogin, UserContext, logOut } from './businessLayer/login';
import { SendForm, syncApp, syncFormUsers, syncWeather, syncRestrictionReason, gravarLogLocal, getCurrentDate } from './businessLayer/forms';
import FormularioTerceiros from './src/FormularioTerceiros';
import DadosOperacao from './src/DadosOperacao';
import DadosVia from './src/DadosVia';
